$( document ).on('turbolinks:load', function() {
    $('#make-selector').change(function() {
      var make_id = $(this).val();
      $.get( "/get_models_by_make", { id: make_id }, function( data ) {
        $("#model-selector").html("");
        $("#order_option_vehicle_id").html("");
        //Include a blank row
        $("#model-selector").append( "<option value label=\" \"</option>" );
        $.each( data, function( index, value ) {
          $("#model-selector").append( "<option value='" + value.id + "'>" + value.model + "</option>" );
        });
      });
    });
  });

  $( document ).on('turbolinks:load', function() {
    // Listen for the model selector changing
    $('#model-selector').change(function() {
      var make_id = $(this).val();
      $.get( "/get_years_by_model", { id: make_id }, function( data ) {
        $("#order_option_vehicle_id").html("");
        //Include a blank row
        $("#order_option_vehicle_id").append( "<option value label=\" \"</option>" );
        $.each( data, function( index, value ) {
          $("#order_option_vehicle_id").append( "<option value='" + value.id + "'>" + value.year + "</option>" );
        });
      });
    });
  });

  function updateMake(content) {
    document.getElementById("preview-make").innerHTML = content;
}

function updateModel(content) {
  document.getElementById("preview-model").innerHTML = content;
}

function updateYear(content) {
  document.getElementById("preview-year").innerHTML = content;
}

function updateFrequency(content) {
  if(content.includes("three_months")) {
    frequency = "Every 3 Months";
  }
  else {
    frequency = "Every 6 months";
  }
  document.getElementById("preview-frequency").innerHTML = frequency;
}

function updateQuality(content) {
  if(content.includes("good")) {
    quality = "Good";
  }
  else if(content.includes("better")) {
    quality = "Better";
  }
  else {
    quality = "Best";
  }
  document.getElementById("preview-quality").innerHTML = quality;
}
